mapper.update:
UPDATE room
SET register_info=#{registerInfo}, address1=#{address1}, address2=#{address2}, deal=#{deal}, price=#{price}, month_price=#{monthPrice}, cost=#{cost}, cost_info=#{costInfo}, move_date=#{moveDate}, whole_class=#{wholeClass}, address2=#{address2}, my_class=#{myClass}, form=#{form}, park=#{park}, choice_option=#{choiceOption}, board_cn=#{boardCn}, zip_code=#{zipCode}
WHERE ROOM_ID=#{roomId}

mapper.select:
SELECT * FROM room
WHERE ROOM_ID=#{roomId}
ORDER BY ROOM_ID DESC
